import React from "react";
import useInput from "../../Hooks/use-input";

import classes from "./Checkout.module.css";

const Checkout = (props) => {
    const isNotEmpty = value => value.trim() !== "";
    
    const {
        value:enteredName,
        isValide: enteredNameIsValide,
        hasError: nameInputHasError,
        valueInputChangeHandler: nameInputChangeHandler,
        valueInputBlurHandler: nameInputBlurHandler,
        reset: resetNameInput
    } = useInput(isNotEmpty)

    const {
        value:enteredStreet,
        isValide: enteredStreetIsValide,
        hasError: streetInputHasError,
        valueInputChangeHandler: streetInputChangeHandler,
        valueInputBlurHandler: streetInputBlurHandler,
        reset: resetStreetInput
    } = useInput(isNotEmpty)

    let formIsValide = false;

    if(enteredNameIsValide && enteredStreetIsValide){
        formIsValide = true;
    }

    const confirmHandler = (event) => {
        event.preventDefault();

        if(!formIsValide){
            return;
        }

        resetNameInput();
        resetStreetInput();
    }

    return (
        <form className={classes.form} onSubmit={confirmHandler}>
            <div className={classes.control}>
                <label htmlFor="name">Your name:</label>
                <input 
                type='text' 
                id="name" 
                onChange={nameInputChangeHandler}
                onBlur={nameInputBlurHandler}
                value={enteredName}
                />
                {nameInputHasError && <p>Please enter a valide name.</p>}
            </div>
            <div className={classes.control}>
                <label htmlFor="street">Street:</label>
                <input 
                type='text' 
                id="street"
                onChange={streetInputChangeHandler}
                onBlur={streetInputBlurHandler}
                value={enteredStreet} 
                />
                {streetInputHasError && <p>Please enter a valide street.</p>}
            </div>
            <div className={classes.control}>
                <label htmlFor="postal">Postal code:</label>
                <input type='text' id="postal" />
            </div>
            <div className={classes.control}>
                <label htmlFor="city">City:</label>
                <input type='text' id="city" />
            </div>
            <div className={classes.actions}>
                <button type="button" onClick={props.onCancel}>Cancel</button>
                <button className={classes.submit}>Confirm</button>
            </div>
        </form>
    );
};

export default Checkout;
